  group_eng varchar(200) DEFAULT NULL COMMENT '拆分扣款组合，用于冲帐',
  fund_code varchar(20) NOT NULL COMMENT '资金源编码',
  fund_name varchar(30) NOT NULL COMMENT '资金源名称',
