DROP TABLE IF EXISTS  wlsddb.mcfe_merchant_recharge_record;
CREATE TABLE `mcfe_merchant_recharge_record` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `channel` varchar(10) DEFAULT NULL COMMENT '充值渠道',
  `amount` decimal(18,2) DEFAULT NULL COMMENT '充值金额',
  `order_number` varchar(30) DEFAULT null COMMENT '订单号',
  `result` varchar(10) DEFAULT '2' COMMENT '返回结果 2初始化 0成功  -1失败  1交易处理中',
  `code` varchar(30) DEFAULT null COMMENT '错误code',
  `msg` varchar(30) DEFAULT null COMMENT '错误信息',
  `pay_plat_order_number` varchar(30) DEFAULT null COMMENT '支付公司订单号',
  `pay_plat_req_no` varchar(30) DEFAULT null COMMENT '支付公司请求号',
  `bank_trx_no` varchar(30) DEFAULT null COMMENT '银行流水号',
  `pay_time` varchar(30) DEFAULT NULL COMMENT '支付时间',
  `delete_flag` tinyint(4) DEFAULT '0' COMMENT '删除标识0正常1删除',
  `created_by` varchar(50) DEFAULT NULL COMMENT '创建用户',
  `created_date` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_by` varchar(50) DEFAULT NULL COMMENT '更新用户',
  `updated_date` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='商户充值记录';

